commits.content:
* {
  box-sizing: border-box;
}
img {
  height: 100%;
  width: 100%;
}

html {
  font-family: "Roboto", sans-serif;
}
html,
body {
  margin: 0;
  padding: 0;
}
select {
  width: 320px;
  height: 40px;
  border-radius: 6px;
  font-weight: 700;
  border: 1px rgb(160, 160, 160) solid;
}
button {
  width: 120px;
  height: 40px;
  font-size: 12px;
  background-color: white;
  border: 1px rgb(160, 160, 160) solid;
  border-radius: 4px;
  font-weight: 700;
  cursor: pointer;
  transition: transform 0.2s;
}
.buttonClick {
  transform: scale(1.1);
}
option {
  padding-top: 5px;
}
.container {
  width: 550px;
  height: 550px;
  display: flex;
  flex-direction: column;
}
.main {
  height: 90px;
  width: 100%;
  background-color: rgb(255, 255, 255);
  display: flex;
  justify-content: space-evenly;
  align-items: center;
}

.colors {
  height: 415px;
  display: flex;
}
.colorBox {
  width: 20%;
  height: 100%;
  background-color: rgb(255, 255, 255);
  transition: background 1s;
  /* border: 1px black solid; */
}
.color_values {
  height: 45px;
  display: flex;
  /* background-color: gray; */
}
.valueBox {
  height: 100%;
  width: 20%;
  /* border: 1px solid black; */
  display: flex;
  justify-content: center;
  align-items: center;
  font-weight: 700;
}
#color-picker {
  padding: 0;
  margin: 0;
  height: 47px;
  width: 55px;
  border: none;
  outline: none;
  -webkit-appearance: none;
  background-color: white;
}
